const INCREASE=document.querySelector("#increase"),DECREASE=document.querySelector("#decrease"),RESET=document.querySelector("#reset"),STEP=document.querySelector("#step"),COUNTER=document.querySelector("#counter");function check_event(e){let t=0,c=e.target,n=Number(STEP.value),r=Number(COUNTER.innerText);isNaN(n)||n<=0||("increase"==c.id&&(t=r+n),"decrease"==c.id&&(t=r-n),"reset"==c.id&&(t=0),COUNTER.innerText=t,set_color(COUNTER.innerText))}function set_color(e){e>0&&(COUNTER.style.color="#198754"),0==e&&(COUNTER.style.color="#fff"),e<0&&(COUNTER.style.color="#dc3545")}INCREASE.addEventListener("click",check_event),DECREASE.addEventListener("click",check_event),RESET.addEventListener("click",check_event),window.addEventListener("load",(()=>STEP.value=1));