const MODAL=document.querySelector("#modal"),DARK_SHADOW=document.querySelector("#darkShadow"),OPEN_MODAL=document.querySelector("#openModal"),CLOSE_MODAL=document.querySelector("#closeModal");function open_modal(){MODAL.classList.add("show"),DARK_SHADOW.classList.add("active")}function close_modal(){MODAL.classList.remove("show"),DARK_SHADOW.classList.remove("active")}OPEN_MODAL.addEventListener("click",open_modal),CLOSE_MODAL.addEventListener("click",close_modal),DARK_SHADOW.addEventListener("click",close_modal);